package JavaConceptsPractice;

public class Divisiblesof5and7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		   for(int i=1;i<=100;i++)
		   {
			   
			   if(i%5==0&&i%7==0)
			   {
				   System.out.println("this number "+i+" is divisible by 5 && 7");
			   }
			   
			   
		   }
		
		
	}

}
